using HTF2018.Backend.Common;
using HTF2018.Backend.Common.Exceptions;
using HTF2018.Backend.Common.Model;
using HTF2018.Backend.Logic.Interfaces;
using System;
using System.Collections.Generic;
using System.Drawing;
using System.Drawing.Imaging;
using System.IO;
using System.Linq;
using System.Threading.Tasks;
using HTF2018.Backend.Logic.Challenges.Helpers;

namespace HTF2018.Backend.Logic.Challenges
{
    public class Challenge20 : ChallengeBase, IChallenge20
    {
        /// <summary>
        /// CHALLENGE 20:
        /// Hidden message in an image, based on pixel manipulation
        /// URL: https://stackoverflow.com/questions/29411265/write-hidden-messages-to-image-file-c-sharp
        /// </summary>
        
        public Challenge20(IHtfContext htfContext, ITeamLogic teamLogic, IChallengeLogic challengeLogic, IDashboardLogic dashboardLogic, IHistoryLogic historyLogic, IImageLogic imageLogic)
            : base(htfContext, teamLogic, challengeLogic, dashboardLogic, historyLogic)
        {
            _htfContext = htfContext;
            _imageLogic = imageLogic;
        }

        private readonly IImageLogic _imageLogic;
        private readonly IHtfContext _htfContext;

        // TODO: Add strings to decode

        private const string Base64Image =
            "";

        private readonly Random _randomGenerator = new Random();
        public async Task<Challenge> GetChallenge()
        {
            var challenge = await BuildChallenge(Identifier.Challenge20);
            return challenge;
        }

        protected override async Task<Question> BuildQuestion()
        {
            var data = BuildAndEncodeImage($"{Guid.NewGuid()}");
            var image = await _imageLogic.StoreImage(data);

            var question = new Question
            {
                InputValues = new List<Value>
                {
                    new Value
                    {
                        Name = "id",
                        Data = $"{image.Id}"
                    },
                    new Value
                    {
                        Name = "image",
                        Data = $"{_htfContext.HostUri}/images/{image.Id}"
                    }
                }
            };
            return question;
        }

        protected override async Task<Answer> BuildAnswer(Question question, Guid challengeId)
        {
            var decoded = await DecodeImage(question);

            return new Answer
            {
                ChallengeId = challengeId,
                Values = new List<Value>
                {
                    new Value{ Name = "decoded", Data = $"{decoded}" }
                }
            };
        }

        protected override async Task<Example> BuildExample(Guid challengeId)
        {
            var data = BuildAndEncodeImage("The end is near,...");
            var image = await _imageLogic.StoreImage(data);

            var question = new Question
            {
                InputValues = new List<Value>
                {
                    new Value
                    {
                        Name = "id",
                        Data = $"{image.Id}"
                    },
                    new Value
                    {
                        Name = "image",
                        Data = $"{_htfContext.HostUri}/images/{image.Id}"
                    }
                }
            };

            return new Example
            {
                Question = question,
                Answer = await BuildAnswer(question, challengeId)
            };
        }

        protected override void ValidateAnswer(Answer answer)
        {
            var invalid = answer.Values == null;
            if (answer.Values != null && answer.Values.Count != 1)
            {
                throw new InvalidAnswerException();
            }
            if (!answer.Values.Any(x => x.Name == "decoded"))
            {
                throw new InvalidAnswerException();
            }
            if (answer.Values.Count(x => x.Name == "decoded") != 1)
            {
                throw new InvalidAnswerException();
            }
            if (string.IsNullOrEmpty(answer.Values.Single(x => x.Name == "decoded").Data))
            {
                throw new InvalidAnswerException();
            }
        }

        #region Helpers

        private byte[] BuildAndEncodeImage(string text)
        {
            //Convert BASE64 to bmp
            var byteBuffer = Convert.FromBase64String(Base64Image);
            using (var memoryStream = new MemoryStream(byteBuffer))
            {
                memoryStream.Position = 0;
                var bitmap = (Bitmap)Image.FromStream(memoryStream);
                return SteganographyHelper.EmbedText(text, bitmap);
            }
        }
        private async Task<string> DecodeImage(Question question)
        {
            var imageId = question.InputValues.Single(x => x.Name == "id").Data;
            var image = await _imageLogic.LoadImage(new Guid(imageId));

            using (var memoryStream = new MemoryStream(image.Data))
            {
                memoryStream.Position = 0;
                var bitmap = (Bitmap)Image.FromStream(memoryStream);
                return SteganographyHelper.ExtractText(bitmap);
            }
        }

        #endregion

    }
    
}